import HttpStatus from 'http-status-codes';
import * as UserService from '../services/user.service';


export const userRegistration = async (req, res, next) => {
  try {
    console.log("aaaaaaaaaaaaaa ",req.body);
    const data = await UserService.userRegistration(req.body);
    res.status(HttpStatus.OK).json({
      code: HttpStatus.OK,
      data:data,
      message: 'User Registered successfully'
    });
  } catch (error) {
    next(error);
  }
};

export const login =async(req,res,next) => {
  try
  {
    const data = await UserService.login(req.body);
    res.status(HttpStatus.OK).json({
      code: HttpStatus.OK,
      token:data,
      message:"Logged in Successfully"
    });
  } catch (error) {
    next(error);
  }
};
/**
 * Controller to get all users available
 * @param  {object} req - request object
 * @param {object} res - response object
 * @param {Function} next
 */
export const getAllUsers = async (req, res, next) => {
  try {
    const data = await UserService.getAllUsers();
    res.status(HttpStatus.OK).json({
      code: HttpStatus.OK,
      data: data,
      message: 'All users fetched successfully'
    });
  } catch (error) {
    next(error);
  }
};


export const Forgotpwd = async (req, res, next) => {
  try {
    const data = await UserService.Forgotpwd(req.body);
    res.status(HttpStatus.ACCEPTED).json({
      code: HttpStatus.ACCEPTED,
      data: data,
      message: 'token has been sent in Email ID'
    })
  }
  catch (error) {
    next(error);
  }
};


//Controller for reset password
export const pwdReset = async (req, res, next) => {
  try
  {
    const data = await UserService.pwdReset(req.params.token, req.body);
    
    res.status(HttpStatus.OK).json({
      code: HttpStatus.OK,
      data: data,
      message: 'Password is Updated.'
    });

  } 
  catch (error)
  {
    next(error);
  }
};

